package Constant;

public class ConstantDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 3;
		float b = 3.0F;
		double c = a+b;
		
		float d = 100.0f;
		int e = 100;
		
		float f = (float) 100.0;
		int g = (int) 500.1f;
		
		System.out.println(f+g);;

	}

}
